import React, { useState } from "react";
import NovelForm from "./NovelForm";
import * as novelApi from "../api/novelsApi";

const ManageNovels = (props) => {
  const [novels, setNovels] = useState({
    id: null,
    slug: "",
    name: "",
    authorId: null,
    description: "",
  });

  function handleChange({ target }) {
    const updateNovels = { ...novels, [target.name]: target.value };
    setNovels(updateNovels);
  }

  function handleSubmit(event) {
    event.preventDefault();
    novelApi.saveNovels(novels).then(() => {
      props.history.push("/novels");
    });
  }

  return (
    <>
      <h2>Manage Novels</h2>
      <NovelForm
        novels={novels}
        onChange={handleChange}
        onSubmit={handleSubmit}
      />
    </>
  );
};

export default ManageNovels;
