import React, { useState, useEffect } from "react";
import { getNovels } from "../api/novelsApi";

function NovelsPage() {
  const [novels, setNovels] = useState([]);

  useEffect(() => {
    getNovels().then((_novel) => setNovels(_novel));
  });

  return (
    <>
      <h2 style={{ marginLeft: "151px", marginTop: "70px" }}>Novels </h2>
      <table
        className="table"
        style={{
          width: "89%",
          marginLeft: "151px",
          border: "5px solid black",
        }}
      >
        <thead>
          <tr>
            <th>Name</th>
            <th>AuthorId </th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {novels.map((novel) => {
            return (
              <tr key={novel.id}>
                <td>{novel.Name}</td>
                <td>{novel.AuthorId}</td>
                <td>{novel.Description}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </>
  );
}

export default NovelsPage;
