import React from "react";
import { getNovels } from "../api/novelsApi";

class NovelsPage extends React.Component {
  state = {
    novel: [],
  };

  componentDidMount() {
    getNovels().then((novel) => this.setState({ novel: novel }));
  }

  render() {
    return (
      <>
        <h2 style={{ marginLeft: "151px", marginTop: "70px" }}>Novels </h2>
        <table
          className="table"
          style={{
            width: "89%",
            marginLeft: "151px",
            border: "5px solid black",
          }}
        >
          <thead>
            <tr>
              <th>Name</th>
              <th>AuthorId </th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>
            {this.state.novel.map((novel) => {
              return (
                <tr key={novel.id}>
                  <td>{novel.Name}</td>
                  <td>{novel.AuthorId}</td>
                  <td>{novel.Description}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </>
    );
  }
}

export default NovelsPage;
