import React from "react";
import image from "../kitchenStory.jpg";

function AboutPage() {
  return (
    <div style={{ paddingLeft: "147px" }}>
      <h1>
        About us:{" "}
        <img src={image} style={{ width: "250px", height: "100px" }} />
      </h1>
      <div
        className="bg-info text-white"
        style={{
          border: "transparent",
          width: "700px",
          backgroundColor: "#E6E6E6",
        }}
      >
        <p
          style={{
            marginLeft: "30px",
            marginRight: "30px",
          }}
        >
          In an effort to help educators and parents during school closures, a
          number of publishers or authors of books in the Novel Effect Library
          have relaxed restrictions for online readings and sharing. The
          majority of titles in the Novel Effect app now fall into this
          category. For more information, see publisher permissions and
          guidelines for recording, streaming, and posting read alouds of their
          titles. For recording or sharing the best online read aloud using
          Novel Effect see our helpful guide, or watch this video.
        </p>
      </div>
    </div>
  );
}

export default AboutPage;
