import React from "react";
import { getCourses } from "../api/courseApi";

class DishesPage extends React.Component {
  state = {
    receipe: [],
  };

  componentDidMount() {
    getCourses().then((receipe) => this.setState({ receipe: receipe }));
  }

  render() {
    return (
      <>
        <h2>Recipe </h2>
        <table className="table">
          <thead>
            <tr>
              <th>DishName</th>
              <th>Cuisine </th>
              <th>Category</th>
            </tr>
          </thead>
          <tbody>
            {this.state.receipe.map((receipe) => {
              return (
                <tr key={receipe.id}>
                  <td>{receipe.DishName}</td>
                  <td>{receipe.cuisineId}</td>
                  <td>{receipe.recipe}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </>
    );
  }
}

export default DishesPage;
